package p2p

import (
	"encoding/hex"
	"testing"
)

func TestBlockPayload_Parse(t *testing.T) {
	DataInString := ""
	var data []byte
	data, err := hex.DecodeString(DataInString)
	if err != nil {
		t.Error(err)
	}
	recvBlock := BlockPayload{}
	err = recvBlock.Parse(data)
	if err != nil {
		t.Error(err)
	}
}
